    ageGroup: '18 & ABOVE',
    vaccineName: 'Covishield',
    vaccinePrice: 'FREE',
    dose1Qty: 0,
    dose2Qty:12
  },
  {
    centerName: 'Bokaro Thermal Township',
    centeAddress: 'HSC GOBINDPUR NEAR BOKARO THERMAL HOSPITAL,BOKARO THERMAL,JHARKHAND,829107',
    ageGroup: '18 & ABOVE',
    vaccineName: 'Covishield',
    vaccinePrice: 'FREE',
    dose1Qty: 0,
    dose2Qty:12
  },
  {
    centerName: 'Phusro Railway Station',
    centeAddress: 'HSC GOBINDPUR NEAR BOKARO THERMAL HOSPITAL,BOKARO THERMAL,JHARKHAND,829107',
    ageGroup: '18 & ABOVE',
    vaccineName: 'Covishield',
    vaccinePrice: 'FREE',
    dose1Qty: 4,
    dose2Qty:12
  },
]

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      error: null,
      isLoaded: false,
      items: []
    };
  }

  componentDidMount() {
    fetch('https://cdn-api.co-vin.in/api/v2/appointment/sessions/public/calendarByDistrict?district_id=253&date=29-07-2021')
      .then(res => res.json())
      .then(
        (data) => {
          this.setState({
            isLoaded: true,
            items: data.centers
          });
        },
        // Note: it's important to handle errors here
        // instead of a catch() block so that we don't swallow
        // exceptions from actual bugs in components.
        (error) => {
          this.setState({
            isLoaded: true,
            error
          });
        }
      )

    // fetch('https://cdn-api.co-vin.in/api/v2/appointment/sessions/public/calendarByDistrict?district_id=242&date=22-07-2021')
    //   .then(response => response.json())
    //   .then(data => console.log(data.centers));
  }

  render() {
    const { items } = this.state;
    let sessionsList = []
      for(let i=0;i<items.length;++i){
        for(let j=0;j<items[i].sessions.length;++j){
          let obj = {...items[i].sessions[j], ...items[i]}
          sessionsList.push(obj);
        }
      }

    //console.log('-------', sessionsList)

    return (
        <Landing/>
        // <HeaderWrapper>COWIN SLOTS</HeaderWrapper>
        // <Row2Wrapper>
        //     <Col2Wrapper>
        //       {sessionsList.map(obj => 
        //         <Card 
        //           centerName={obj.name}
        //           centeAddress={`${obj.address}, ${obj.district_name}, ${obj.state_name}, ${obj.pincode}`}
        //           ageGroup={'18 & Above'}
        //           vaccineName={obj.vaccine}
        //           vaccinePrice={obj.fee_type}
        //           dose1Qty={obj.available_capacity_dose1}
        //           dose2Qty={obj.available_capacity_dose2}
        //         />
        //       )}
        //     </Col2Wrapper>
        //     <Col2Wrapper>
        //       <Modi/>
        //     </Col2Wrapper>
        // </Row2Wrapper>

    );
  }
}

export default App;
