import React from 'react';
import styled from 'styled-components';
import CrossLogo from '../static/red-cross.png';

const CardWrapper = styled('div')`

    border: 1px solid #c7c2c2;
    background: #fff;
    margin: 10px;
    min-height: 100px;
    border-radius: 10px;
    padding: 10px 10px;

    @media(min-width: 768px) {
    	width: 70%;
    }
`

const IconWrapper = styled('img')`
	height: 15px;
	width: 20px;
`
const RowWrapperUtil = styled('div')`
	display: flex;
	flex-direction: row;
`

const ColWrapperUtil = styled('div')`
	display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    padding: 0 5px;
`

const Text2Wrap = styled('div')`
	font-size:15px;
	font-weight:bold;
	color:#000066;
	display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;

`
const TextWrap = styled('div')`
	font-size:15px;
	font-weight:bold;
	color:#000066;

`
const QtyWrapper = styled('div')`
	font-size: 14px;
	font-weight: 900;
	color: green;
`
const Qty1Wrapper = styled('div')`
	font-size: 14px;
	font-weight: 900;
	color: orange;
`


const ParaWrap = styled('div')`
	font-size:12px;
	color:grey;
`
const ButtonWrap = styled('div')`
	border: 1px solid #007bff;
    color: white;
    font-size: 10px;
    background:green;
    border-radius:5px;
    font-weight:bold;
    height:25px;
    display: flex;
    flex-direction: column;
    text-align: center;
    justify-content: center;
    padding: 0 8px;

`
const DoseWrap = styled('div')`
	margin-left:100px;
`
const Row = styled('div')`
	display:  flex;
	flex-direction: row;
	border : 1px solid white;
    margin-top:5px;
    justify-content: space-between;
    padding: 7px;
`
const Col = styled('div')`
	display:  flex;
	flex-direction: column;
	border : 1px solid white;
	width:30%;
`
const RowWrapper = styled('div')`
	display:  flex;
	flex-direction: row;
	border : 1px solid white;
	justify-content: flex-end;
	font-weight:bold;
`
const ColWrapper = styled('div')`
	display:  flex;
	flex-direction: row;
	border : 1px solid white;
`
const ButWrap = styled('div')`
	border : 1px solid white;
	background:blue;
	color:white;
	font-size:15px;
	font-weight:bold;
	border-radius:5px;
	text-align:center;
	display: flex;
    flex-direction: column;
    justify-content: center;
    padding: 10px;

    @media (max-width: 768px) {
    	width: 90px;
    }
`

function renderDoseQuantity(dose1Qty) {
	if (dose1Qty == 0)
		return <IconWrapper src={CrossLogo}/>
	if (dose1Qty >= 10)
		return <QtyWrapper>{dose1Qty}</QtyWrapper>
	return <Qty1Wrapper>{dose1Qty}</Qty1Wrapper>
}

const Card = ({centerName, centeAddress, ageGroup, vaccineName, vaccinePrice, dose1Qty}) => (
	<CardWrapper>
        <TextWrap>{centerName}</TextWrap>
        <Row>
			<Col>
			   <ParaWrap>{centeAddress}</ParaWrap> 
			</Col>
			<Col>
			   <RowWrapper>{vaccineName}</RowWrapper>
			   <RowWrapper>
			        <ButtonWrap>{vaccinePrice}</ButtonWrap>
			   </RowWrapper>
			</Col>
        </Row>
        <Row>
           <ColWrapper>
                <ButWrap>{ageGroup}</ButWrap>
           </ColWrapper>
           <ColWrapper>
           		<RowWrapperUtil>
           			<ColWrapperUtil>
	                	<Text2Wrap>DOSE 1</Text2Wrap>
	                </ColWrapperUtil>
	                <ColWrapperUtil>
	                	{renderDoseQuantity(dose1Qty)}
	                </ColWrapperUtil>
                </RowWrapperUtil>
           </ColWrapper>
           <ColWrapper>
           		<RowWrapperUtil>
           			<ColWrapperUtil>
	                	<Text2Wrap>DOSE 2</Text2Wrap>
	                </ColWrapperUtil>
	                <ColWrapperUtil>
	                	<IconWrapper src={CrossLogo}/>
	                </ColWrapperUtil>
                </RowWrapperUtil>
           </ColWrapper>

        </Row>


	</CardWrapper>
)

export default Card;
