import React from 'react';
import Dropdown from 'react-dropdown';
import 'react-dropdown/style.css';

import styled from 'styled-components';

const RowWrapper=styled('div')`
    text-align:center;
    margin: 136px 34px;


`
const DropdownWrapper=styled(Dropdown)`


`

const Heading = styled('div')`
	font-size: x-large;
    font-weight: bold;
    color: darkblue;
`
const Row=styled('div')`
    display:  flex;
    flex-direction: row;
    border : 1px solid white;
    justify-content: space-evenly;
    margin:5px;
    padding: 7px;


`
const Col=styled('div')`
    display:  flex;
    flex-direction: column;
    border : 1px solid white;
    margin:5px;
    padding: 7px;
    width: 27%;
`
const ButtonWrapper = styled('button')`
    background-color: #4CAF50;
    border-radius: 10px;
    height: 33px;
    width: 39%;
    display: flex;
    font-weight:bold;
    flex-direction: row;
    justify-content: center;
    padding: 5px;
    font-size: 15px;
    background: darkblue;
    color: white;

`


const myMap = {
  'JHARKHAND': ['bokaro', 'deoghar', 'dhanbad'],
  'DELHI': ['pinjra', 'igate', 'dronepur'],
  'BIHAR': ['jon', 'mirza', 'dehli']
}

const options = [
  'JHARKHAND', 'BIHAR', 'DELHI','KERELA','PUNE'
];

class Landing extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      statesList: options,
      districtList: [],
      selectedState: null,
      selectedDistrict: null
    };
  }

  stateChange = e => {
    console.log('<><><>', e)
    fetch(' https://cdn-api.co-vin.in/api/v2/admin/location/districts/5')
      .then(res => res.json())
      .then(
        (data) => {
          this.setState({
            districtList: data.districts
          })
        },
        // Note: it's important to handle errors here
        // instead of a catch() block so that we don't swallow
        // exceptions from actual bugs in components.
        (error) => {
          this.setState({
            isLoaded: true,
            error
          });
        }
      )

    this.setState({
      selectedState: e.value,
      districtList: myMap[e.value],
    })
  }

  districtChange = e => {
    this.setState({selectedDistrict: e.value})
  }

  render() {
    const defaultState = 'SELECT STATE';
    const defaultDistrict='SELECT DISTRICT';
    const currentState = this.state.selectedState ? this.state.selectedState: defaultState;
    const currentDistrict = this.state.selectedDistrict ? this.state.selectedDistrict: defaultDistrict;
    const districtOptions = this.state.districtList;
    console.log(myMap);

    return (
      <>
      <RowWrapper>
          <Heading>Check Your Nearest Vaccination Center And Slots Availability</Heading>
      </RowWrapper>
      <Row>  
        <Col> 
         <DropdownWrapper options={options} onChange={(e) => this.stateChange(e)} value={currentState} />
        </Col>
        <Col> 
         <DropdownWrapper options={districtOptions} onChange={(e) => this.districtChange(e)} value={currentDistrict} />
        </Col>
        <Col> 
         <ButtonWrapper>Search</ButtonWrapper>
        </Col>
      </Row>
      </>
    )    
  }
}
export default Landing;
